mapper.select:
		select * from c_arr_class


                order by ${sidx} ${order}


                order by arr_class_id desc



			limit #{offset}, #{limit}

SELECT w.week_id,IF(t.remainNumber IS NULL,(SELECT COUNT(0) from c_classtime),t.remainNumber ) as remainNumber from (SELECT arr.week_id,(SELECT COUNT(0) from c_classroom)-COUNT(arr.classroom_id) as `remainNumber` from c_arr_class arr right JOIN c_classtime time on arr.classtime_id = time.classtime_id
and arr.classtime_id=#{classtimeId}
and arr.tenant_id=#{tenantId}
GROUP BY arr.week_id) t RIGHT JOIN weeks w on w.week_id=t.week_id ORDER BY week_id ASC

SELECT t.course_name as courseName,
weeks.week_id as weekId
from 
(SELECT course.course_name,arr.week_id from c_arr_class arr
JOIN c_course course ON course.course_id=arr.course_id
JOIN pl_teacher teacher on teacher.teacher_id = course.teacher_id


		and arr.tenant_id=#{tenantId}


		and teacher.teacher_id = #{teacherId}


		and arr.classtime_id = #{classtimeId}

		and course.status != 2

) t
RIGHT JOIN weeks ON weeks.week_id=t.week_id
